mapper.select:

select 			
	n_num, e_id, n_title, n_contents, n_regdate, n_moddate
from ( 
	 select /*+INDEX_DESC(notice pk_notice) */
		rownum rn, n_num, e_id, n_title, n_contents, n_regdate, n_moddate
	from 
		notice 
	where rownum <= #{pageNum} * #{amount}
	)
where rn > (#{pageNum} -1) * #{amount}
